import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Headlines.css";

export default function Headlines() {
  const [news, setNews] = useState([]);

  const [showSummary, setShowSummary] = useState(false);
  const [selectedNews, setSelectedNews] = useState(null);

  useEffect(() => {
    axios
      .get(
        `https://content.guardianapis.com/search?api-key=3076f285-1c53-44a2-912a-10878f5ea096&q=news&show-fields=thumbnail`
      )
      .then((res) => {
        const data = res.data.response.results.slice(0, 10);
        setNews(data);
      })
      .catch((err) => console.log(err));
  }, []);

  const handleNewsClick = (news) => {
    setSelectedNews(news);
    setShowSummary(true);
  };

  const handleCloseSummary = () => {
    setShowSummary(false);
    setSelectedNews(null);
  };

  return (
    <div className="TheGuardian">
      {showSummary && selectedNews && (
        <div className="news-summary">
          <button onClick={handleCloseSummary}>Close</button>
          <img
            src={selectedNews.fields.thumbnail}
            alt={selectedNews.webTitle}
          />
          <h2>{selectedNews.webTitle}</h2>
          <p>{selectedNews.fields.body}</p>
        </div>
      )}
      <ul>
        {news.map((item) => (
          <li key={item.id}>
            <a href={item.webUrl} target="_blank" rel="noreferrer">
              <img src={item.fields.thumbnail} alt={item.webTitle} />
              <h2 onClick={() => handleNewsClick(item)}>{item.webTitle}</h2>
              <p>{item.fields.trailText}</p>
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}
