import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function Article() {
  const [article, setArticle] = useState({});
  const { id } = useParams();

  const slugify = (str) => {
    return str.replace(/~/g, "/");
  };

  useEffect(() => {
    axios
      .get(
        `https://content.guardianapis.com/${id}?api-key=3076f285-1c53-44a2-912a-10878f5ea096&show-fields=body`
      )
      .then((response) => {
        console.log(response);
        setArticle(response.data.response.content.fields);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [id]);

  return (
    <div>
      {id}
      <h1>{article.headline}</h1>
      <p>{article.body}</p>
    </div>
  );
}

export default Article;
