import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function HeadlineItem({ item }) {
  const [expanded, sEtexpanded] = useState(false);
  return (
    <div>
      <li>
        <Link to={`/news/${item.id}`} target="_blank" rel="noreferrer">
          <img src={item.fields.thumbnail} alt={item.webTitle} />
          <h2>{item.webTitle}</h2>
          {/* <p>{item.fields.trailText}</p> */}
        </Link>
      </li>
      {expanded ? <p>{item.fields.bodyText}</p> : null}
    </div>
  );
}
