import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Headlines.css";

export default function Headlines() {
  const [news, setNews] = useState([]);

  useEffect(() => {
    axios
      .get(
        `https://content.guardianapis.com/search?api-key=3076f285-1c53-44a2-912a-10878f5ea096&q=news&show-fields=thumbnail`
      )
      .then((res) => {
        const data = res.data.response.results.slice(0, 10);
        setNews(data);
      })
      .catch((err) => console.log(err));
  }, []);

  const handleItemClick = (e, item) => {
    e.preventDefault();
    const summary = item.fields.trailText;
    item.fields.summary = summary;
    setNews([...news]);
  };

  return (
    <div className="TheGuardian">
      <ul>
        {news.map((item) => (
          <li key={item.id}>
            <a
              href={item.webUrl}
              target="_blank"
              rel="noreferrer"
              onClick={(e) => handleItemClick(e, item)}
            >
              <img src={item.fields.thumbnail} alt={item.webTitle} />
              <h2>{item.webTitle}</h2>
              {item.fields.summary && <p>{item.fields.summary}</p>}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}
